view.script:
function formUpdateFrameSave(){
    manager.doAjax('structure/frame/updateFrame', function(response){
        if (response.status == 'error') {
            theme.message('error','Error',response.message);
        } else {
            theme.dialog.refresh('formUpdateFrameDialog', {
                toolbar: null,
                width: 510,
                url: 'fnbr20/entry/gridUpdate/' + $('#frame_entry').textbox('getValue')   
            });
        }
    }, 'formUpdateFrame');
}
